import React, { Component } from 'react';
import ToDodata from './ToDoList/components/todoitem/todoItems';
import AddItem from './ToDoList/components/AddItem/AddItem'
import CompletedList from './ToDoList/components/Completed/list';
import DeletedList from './ToDoList/components/Deleted/list';



export const MyContext = React.createContext()

class App extends Component {
  state = {
    data: [{ name: "samar", id: 1, isCompleted: true, isDeleted: false }, { name: "samfgar", id: 2, isCompleted: false, isDeleted: true }],
  }
  // AllData = this.state.data.filter(item => (!item.isCompleted && !item.isDeleted))
  CompletedData = this.state.data.filter(item => (item.isCompleted && !item.isDeleted));
  DeletedData = this.state.data.filter(item => item.isDeleted)
  addTodo = (todo) => {

    const { data } = this.state;
    this.setState({ data: data.concat(todo) })
  }
  RedoItem = (e) => {
    let data = this.state.data.filter(item => {
      const id = e.target.dataset.id
      if (item.id === id) {
        item['isCompleted'] = false;
        return item;
      } else {
        return item;
      }
    }

    )
    this.setState({ data });

  }
  deleteItem = (id) => {
    const { data } = this.state;
    let items = data.filter(item => {
      if (item.id === id) {
        item['isDeleted'] = true;
        return item;
      } else {
        return item;
      }
    }

    )
    this.setState({ data: items });
  }
  doneItem = (id) => {
    let data = this.state.data.filter(item => {
      if (item.id === id) {
        item['isCompleted'] = true;
        return item;
      } else {
        return item;
      }
    }

    )
    this.setState({ data });

  }
  RestoreItem = (id) => {
    let data = this.state.data.filter(item => {
      if (item.id === id) {
        item['isDeleted'] = false;
        return item;
      } else {
        return item;
      }
    }

    )
    this.setState({ data });

  }


  Myvalue ={ state: this.state, addTodo: this.addTodo, deleteItem: this.deleteItem, doneItem: this.doneItem, RedoItem: this.RedoItem, CompletedData: this.CompletedData, restoreItem: this.RestoreItem, deletedData: this.DeletedData, allData: this.AllData };
  render() {
    // console.log(this.DeletedData);
    return (
      <MyContext.Provider value={{ state: this.state, addTodo: this.addTodo, deleteItem: this.deleteItem, doneItem: this.doneItem, RedoItem: this.RedoItem, CompletedData: this.CompletedData, restoreItem: this.RestoreItem, deletedData: this.DeletedData}}>

        <div className="App container">
          <div>
            <ToDodata />
            <AddItem />
          </div>
          <div className="Complete">
            <CompletedList done={this.doneItem}/>
            <DeletedList />
          </div>


        </div>
      </MyContext.Provider>
    );
  }
}

export default App;
