import React from 'react';
import { connect } from 'react-redux'
import { GetsData } from './../../actions/tasks';
import { GetsCompleted } from './../../actions/tasks';



class ToDodata extends React.Component {

    deleteItem = (id) => {
        let items = this.props.data.filter(item => {
            return item.id !== id
        })
        this.props.setData(items);
    }

    DoneItem = (id) => {
        let items = this.props.data.filter(item => {
            if(item.id === id){
                item['isCompleted'] = true;
                this.deleteItem(id);
                return item;
            }else{
                return item;
            }    
        }

        )
        this.props.setData(items);
        // console.log(this.props.data)

    }

    render() {

        const { data } = this.props;
        const length = data.length;
        const listItems = length ? (
            data.map(item => {
                return (
                    <div key={item.id}>
                        <span className="name ">{item.name}</span>
                        <span className="action icon" onClick={() => this.deleteItem(item.id)}>&times;</span>
                        <span className="action icon" onClick={() => this.DoneItem(item.id)}>&radic;</span>
                    </div>
                )
            })
        ) : (
                <p>No Tasks to show</p>
            )
        return (
            <div className="ListItems">
                <span className="name title">Task Name</span>
                <span className="action title">Delete</span>
                <span className="action title">Complete</span>
                {listItems}
            </div>
        )
    }
}

const mapStateToProps = state => ({
    data: state.items,
});


const mapDispatchToProps = dispatch => ({
    setData: data => dispatch(GetsData(data)),
    setCompletedlist: data => dispatch(GetsCompleted(data)),
});


export default connect(mapStateToProps, mapDispatchToProps)(ToDodata);
