import React from 'react';
import './todoItems.css';
import { connect } from 'react-redux'
import { GetsData } from './../../actions/tasks';



class ToDoItems extends React.Component {

    render() {
        const  items = this.props.setData();
        let length = items.length;
        const listItems = length ? (
            items.map(item => {
                return (
                    <div key={item.id}>
                        <span className="name ">{item.name}</span>
                        <span className="action icon" >&times;</span>
                        <span className="action icon" >&radic;</span>
                    </div>
                )
            })
        ) : (
                <p>No Tasks to show</p>
            )
        return (
            <div className="ListItems">
                <span className="name title">Task Name</span>
                <span className="action title">Delete</span>
                <span className="action title">Complete</span>
                {listItems}
            </div>
        )
    }
}

const mapStateToProps = state => ({
    data: state.data,
});


const mapDispatchToProps = dispatch => ({
    setData: data => dispatch(GetsData(data)),
});


export default connect(mapStateToProps, mapDispatchToProps)(ToDoItems);
