import React from 'react';
import { connect } from 'react-redux';
class DeletedList extends React.PureComponent {

    RestoreItem = (id) => {
        let items = this.props.data.filter(item => {
            if (item.id === id) {
                item['isDeleted'] = false;
                return item;
            } else {
                return item;
            }
        }

        )
        this.props.setData(items);

    }
    render() {
        const { DeletedData } = this.props;
        console.log(this.props.DeletedData)
        // debugger
        const CompleteItems = (
            DeletedData.map(item => {
                return (
                    <div key={item.id}>
                        <span className="name ">{item.name}</span>
                        <span className="action icon" onClick={() => this.RestoreItem(item.id)}>&radic;</span>
                    </div>
                )

            })
        )

        return (
            <>
                <div className="completedList">
                    <span className="name title">Deleted</span>
                    <span className="action title">Restore</span>
                    {CompleteItems}
                </div>

            </>

        )

    }
}


const mapStateToProps = state => {
    return {
        data: state.items,
        DeletedData: state.items.filter(item => item.isDeleted)
    }
};


const mapDispatchToProps = dispatch => ({

});


export default connect(mapStateToProps, mapDispatchToProps)(DeletedList);
