import React, { Component } from 'react';
import ToDoItems from './ToDoList/components/todoitem/todoItems';
import AddItem from './ToDoList/components/AddItem/AddItem'
import CompletedList from './ToDoList/components/Completed/list';
import DeletedList from './ToDoList/components/Deleted/list';



export const MyContext = React.createContext({ data: [] });
class App extends Component {
  state = {
    data: [],
  }

  addTodo = (todo) => {
    const { data } = this.state;
    this.setState({ data: [...data, todo] })
  }

  render() {
    return (
      <MyContext.Provider value={{ items: [] }}>
        <div className="App container">
          <div>
            <ToDoItems />
            <AddItem />
          </div>
          <div className="Complete">
            <CompletedList />
            <DeletedList />
          </div>


        </div>
      </MyContext.Provider>
    );
  }
}

export default App;
