import React, { Component } from 'react';
import uuidv4 from 'uuid/v4';
import {MyContext} from './../../../App'

class AddItem extends Component {

    handleSubmit = (addTodo) => (e) => {
        e.preventDefault();
        if (this.refs.input.value === '') return;
        addTodo({ id: uuidv4(), name: this.refs.value, isCompleted: false, isDeleted: false });
        this.refs.input.value = '';
    }

    render() {
        return (
            <MyContext.Consumer>
                {value => (
                    <>
                    <div>
                        <form onSubmit={this.handleSubmit(value.addTodo)}>
                            <input ref="input" type="text" id="name" placeholder="Enter Task Name..." />
                            <input className="add" type="submit" value="ADD" />

                        </form>
                    </div>
                    </>

                )}
            </MyContext.Consumer>

        )
    }

}

export default AddItem;
