import React, { Component } from 'react';
import uuidv4 from 'uuid/v4'
import { GetsData ,addToList } from './../../actions/tasks';

class AddItem extends Component {

    handleSubmit = (e) => {
        e.preventDefault();
        if (this.refs.input.value === '') return;
        this.props.addToList({ id: uuidv4(), name: this.refs.input.value, isCompleted: false, isDeleted: false });
        this.refs.input.value = '';
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input ref="input" type="text" id="name" placeholder="Enter Task Name..." />
                    <input className="add" type="submit" value="ADD" />

                </form>
            </div>
        )
    }

}

export default AddItem;
