import React from 'react';
import { connect } from 'react-redux';
import { GetsData } from './../../actions/tasks';

class CompletedList extends React.PureComponent {

    deleteItem = (id) => {
        let items = this.props.CompletedData.filter(item => {
            if(item.id === id){
                item['isDeleted'] = true;
                return item;
            }else{
                return item;
            }    
        }

        )
        this.props.setData(items);
    }

    RedoItem = (id) => {
        let items = this.props.data.filter(item => {
            if (item.id === id) {
                item['isCompleted'] = false;
                return item;
            } else {
                return item;
            }
        }

        )
        this.props.setData(items);

    }

    render() {
        const { CompletedData } = this.props;
        console.log(this.props.CompletedData)
        // debugger
        const CompleteItems = (
            CompletedData.map(item => {
                return (
                    <div key={item.id}>
                        <span className="name ">{item.name}</span>
                        <span className="action2 icon" onClick={() => this.RedoItem(item.id)}>&radic;</span>
                        <span className="action2 icon" onClick={() => this.deleteItem(item.id)}>&times;</span>

                    </div>
                )

            })
        )

        return (
            <>
                <div className="completedList">
                    <span className="name title">Complete</span>
                    <span className="action2 title">Redo</span>
                    <span className="action2 title">Delete</span>
                    {CompleteItems}
                </div>

            </>

        )

    }
}


const mapStateToProps = state => {
    return {
        data: state.items,
        CompletedData: state.items.filter(item => item.isCompleted)
    }
};


const mapDispatchToProps = dispatch => ({
    setData: data => dispatch(GetsData(data)),
});


export default connect(mapStateToProps, mapDispatchToProps)(CompletedList);
