import React, { Component } from 'react';
import './AddItems.css'
import { connect } from 'react-redux'
import { GetsData } from './../../actions/tasks';

class AddItem extends Component {

    handleChange = (e) => {
        var res = this.refs.input.value;
        return res;

    }
    handleSubmit = (e) => {
        const id = Math.random*10;
        e.preventDefault();
        let items =this.props.data;
        items.push({id:id,name:this.handleChange()})
        this.props.setData(items)
        console.log(this.props.data);
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input ref="input" type="text" id="name" placeholder="Enter Task Name..." onChange={this.handleChange} />
                    <input className="add" type="submit" value="ADD" />

                </form>
            </div>
        )
    }

}

const mapStateToProps = state => ({
    data: state.items
});


const mapDispatchToProps = dispatch => ({
    setData: data => dispatch(GetsData(data)),
});


export default connect(mapStateToProps, mapDispatchToProps)(AddItem);
