export default (state, action) => {
    switch (action.type) {
      case 'Set-Task-Data':
        return { ...state, items: action.data };
        case 'Set-Complete-Data':
        return { ...state, isCompleted: !state.isCompleted };
      default:
        return state;
    }
  };
  

  state.map(todo =>
    (todo.id === action.id) 
      ? {...todo, completed: !todo.completed}
      : todo
  )
