import React from 'react';
import { MyContext } from './../../../App'


class ToDodata extends React.Component {
    componentDidMount() {
        deleteItem = (id) => {
            const { data } = this.state;
            let items = data.filter(item => {
                if (item.id === id) {
                    item['isDeleted'] = true;
                    return item;
                } else {
                    return item;
                }
            }

            )
            this.setState({ data: items });
        }

        doneItem = (id) => {
            let data = this.state.data.filter(item => {
                if (item.id === id) {
                    item['isCompleted'] = true;
                    return item;
                } else {
                    return item;
                }
            }

            )
            this.setState({ data: data });
            // console.log(this.props.data)

        }


    }
    render() {
        let contextValue = this.context;
        let data = contextValue.state.data;
        const length = data.length;
        const ListItems = length ? (
            data.map(item => {
                return (
                    <div key={item.id}>
                        <span className="name ">{item.name}</span>
                        <span className="action icon" onClick={() => contextValue.deleteItem(item.id)}>&times;</span>
                        <span className="action icon" onClick={() => contextValue.DoneItem(item.id)}>&radic;</span>
                    </div>
                )
            })
        ) : (
                <p>No Tasks to show</p>
            )


        return (
            <div className="ListItems">
                <span className="name title">Task Name</span>
                <span className="action title">Delete</span>
                <span className="action title">Complete</span>
                {ListItems}
            </div>

        )
    }
}


ToDodata.contextType = MyContext;
export default ToDodata;
