import React, { Component } from 'react';
import './AddItems.css'
import { connect } from 'react-redux'
import { GetAddData } from './../../actions/tasks';

class AddItem extends Component {

    addItem = (item) => {
        item.id = Math.random;
        let items = this.props.Ddata;
        items.push(item);
        this.props.data = items
    }
    handleChange = (e) => {
        const  {Ddata} = this.props;
        Ddata.setState({
            [e.target.id]: e.target.value
        })


    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.addItem(this.props.Ddata);

    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" id="name" placeholder="Enter Task Name..." onChange={this.handleChange} />
                    <input className="add" type="submit" value="ADD" />

                </form>
            </div>
        )
    }

}

const mapStateToProps = state => ({
    Ddata: state.AddItem,
});


const mapDispatchToProps = dispatch => ({
    setData: Ddata => dispatch(GetAddData(Ddata)),
});


export default connect(mapStateToProps, mapDispatchToProps)(AddItem);
