import React, { Component } from 'react';
import './AddItems.css'
import { connect } from 'react-redux'
import { GetsData } from './../../actions/tasks';

class AddItem extends Component {

    addItem = (item) => {
        let items = this.props.data;
        items.push(item);
        this.props.setData(items) ;
    }
    handleChange = (e) => {
        // this.props.setData({[e.target.id]: e.target.value});
        let obj = {[e.target.id]: e.target.value};

    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.addItem(this.handleChange.obj);

    }


    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" id="name" placeholder="Enter Task Name..." onChange={this.handleChange} />
                    <input className="add" type="submit" value="ADD" />

                </form>
            </div>
        )
    }

}

const mapStateToProps = state => ({
    data: state.items
});


const mapDispatchToProps = dispatch => ({
    setData: data => dispatch(GetsData(data)),
});


export default connect(mapStateToProps, mapDispatchToProps)(AddItem);
