import React, { Component } from 'react';
import './AddItems.css'

class AddItem extends Component {
    state = {
        name: '',
        age: ''
    }
    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    handleSubmit = (e) =>{
        e.preventDefault();
        this.props.addItem(this.state);
        this.setState({
            name:'',
            age:''
        })

    }

    render() {
        return (
            <div>
                <form  onSubmit={this.handleSubmit}>
                    <input type="text" id="name" placeholder="Enter Task Name..." onChange={this.handleChange} value={this.state.name}/>
                    <input type="submit" value="ADD" />

                </form>
            </div>
        )
    }

}

export default AddItem;
